function adminLogout() {
	if (confirm("로그아웃 하시겠습니까?") == true) {
		location.href = '/member/logout';
	}else {
		return;
	}
}


//헤메뉴 클릭 시 li 태그에 active 클래스 추가
var li = document.querySelectorAll('.navbar-nav .nav-item');
var pathname = window.location.pathname;
if (pathname.includes('/admin/home')) {
	li[0].className += ' active';
} else if (pathname.includes('/admin/member')) {
	li[1].className += ' active';
} else if (pathname.includes('/admin/point')) {
	li[2].className += ' active';
} else if (pathname.includes('/admin/paymentdetail')) {
	li[3].className += ' active';
} else if (pathname.includes('/admin/refund')) {
	li[4].className += ' active';
} else if (pathname.includes('/admin/withdraw')) {
	li[5].className += ' active';
} else if (pathname.includes('/admin/party')) {
	li[6].className += ' active';
} else if (pathname.includes('/admin/category')) {
	li[7].className += ' active';
} else if (pathname.includes('/admin/inquirylist')) {
	li[8].className += ' active';
} else if (pathname.includes('/admin/faq/faqlist')) {
	li[8].className += ' active';
} else if (pathname.includes('/admin/videoList')) {
	li[8].className += ' active';
} 